import "../css/Home.css"
import {maps} from "../controller/maps"
import { djiskstra } from "../controller/djikstra"
import { maps2 } from "../notes"
import { Loading } from "../components/loading"
import { useEffect, useState } from "react";

export function Home(){

  // var listCity = []
  // for (let city of maps) {
  //   if(!listCity.includes(city.startCity)){
  //     listCity.push(city.startCity)
  //   }
  //   if(!listCity.includes(city.finishCity)){
  //     listCity.push(city.finishCity)
  //   }
  // }
  var [listCity,setListCity] = useState([])
  var [isLoading, setIsLoading] = useState(true);
  useEffect(function(){
    setTimeout(function(){
      var newListCity = []
      for (let city of maps2) {
        newListCity.push(city.city_name)
      }
      // console.log(listCity);
      newListCity.sort();
      setListCity(listCity = newListCity)
      setIsLoading(isLoading = false)
    },1000)

  },[])

  function googleMaps(route){
    var output = ""
    for (let char of route) {
     if(char === "-"){
      output += "/"
     } 
     else{
      output += char
     }
    }
    window.open(`https://www.google.co.id/maps/dir/${output}`)
  }




  var [result,setResult] = useState([])

  return (
    <div className="home">
      <nav>
        <h5>Djiskstra</h5>
      </nav>
          {(isLoading == true)? Loading():null}
          <div className="box-form" style= { (isLoading==true) ? { display:"none" } : null }  >
            <span>
              <p>Your Location</p>
              <select name="start-city-input" className="form-select" aria-label="Default select example">
                {
                  listCity.map(function(city){
                    return (<option value={city} key={`${city}-${city}`}>{city}</option>)
                  })
                }
              </select>
            </span>
            <span>
              <p>Destination</p>
              <select name="finish-city-input" className="form-select" aria-label="Default select example">
                {
                  listCity.map(function(city){
                    return (<option value={city} key={`${city}-${city}`}>{city}</option>)
                  })
                }
              </select>
            </span>

            <button className="btn btn-primary" onClick={ function(){
                setIsLoading(isLoading = true)
                var startCityInput = document.getElementsByName('start-city-input')[0].value
                var finishCityInput = document.getElementsByName('finish-city-input')[0].value

                setTimeout(function(){
                  setResult(result = djiskstra(
                    maps,
                    startCityInput,
                    finishCityInput
                    )
                  ) 
                  setIsLoading(isLoading = false )
                },1000)


              }
              // window.location.href = `https://www.google.co.id/maps/dir/${document.getElementsByName('start-city-input')[0].value}/${document.getElementsByName('finish-city-input')[0].value}`
              } >Get shortest route
            </button>


              {
                (result.length > 0)?
                  <table className="table">
                    <thead className="table" style={{ backgroundColor: "rgb(255, 192, 203)",borderColor: "rgb(255, 75, 105)" }}>
                      <tr>
                        <td>Route</td>
                        <td>Distance</td>
                        <td></td>
                      </tr>
                    </thead>
                    {result.map(function(node)
                    {
                        return (
                          <>

                            <tbody className="">
                              <tr className="" key={node.route}>
                                <td>{node.route}</td>
                                <td>{node.distance} Km</td>
                                <td><button onClick={function(){googleMaps(node.route)}} className="btn btn-outline-dark">Detail Route</button></td>
                              </tr>
                            </tbody>
                          </>
                        )
                    })
                    }
                  </table>
                : null
              }
          </div>
    </div>
  )
}
