export function merge(left, right) {
  let arr = []
  while (left.length && right.length) {
      if (left[0]['distance'] < right[0]['distance']) {
      // if (left < right) {
          arr.push(left.shift())  
      } else {
          arr.push(right.shift()) 
      }
  }

  return [ ...arr, ...left, ...right ]
}

export function mergeSort(array) {
  const half = array.length / 2
  if(array.length < 2){
    return array 
  }

  const left = array.splice(0, half)
  return merge(mergeSort(left),mergeSort(array))
}

export function merge_variable(left, right) {
  let arr = []
  while (left.length && right.length) {
      if (left.value < right.value) {
      // if (left < right) {
          arr.push(left.shift())  
      } else {
          arr.push(right.shift()) 
      }
  }

  return [ ...arr, ...left, ...right ]
}

export function mergeSort_variable(array) {
  const half = array.length / 2
  if(array.length < 2){
    return array 
  }

  const left = array.splice(0, half)
  return merge_variable(mergeSort_variable(left),mergeSort_variable(array))
}


export function bubbleSort(arr){

  var i, j;
  var len = arr.length;

  var isSwapped = false;

  for(let i =0; i < len; i++){

    isSwapped = false;

    for(let j = 0; j < len; j++){
      try {
        if(arr[j].value > arr[j + 1].value){
          var temp = arr[j]
          arr[j] = arr[j+1];
          arr[j+1] = temp;
          isSwapped = true;
        }
      } catch (error) {

      }

    }

    // IF no two elements were swapped by inner loop, then break

    if(!isSwapped){
      break;
    }
  }

  // Print the array
  return (arr)
}
